const UAFlag = () => (
	<svg
		className="w-5 h-4 rounded-sm"
		viewBox="0 0 640 480"
		xmlns="http://www.w3.org/2000/svg"
	>
		<g fillRule="evenodd" strokeWidth="1pt">
			<path fill="#ffd500" d="M0 0h640v480H0z" />
			<path fill="#005bbb" d="M0 0h640v240H0z" />
		</g>
	</svg>
);

const languages = [
	{ code: 'en', label: 'English', FlagComponent: USFlag },
	{ code: 'uk', label: 'Українська', FlagComponent: UAFlag },
];

export default function LanguageSwitcher() {
	const { i18n } = useTranslation();

	const currentLanguage =
		languages.find((lang) => lang.code === i18n.language) || languages[0];
	const CurrentFlag = currentLanguage.FlagComponent;

	return (
		<Select.Root
			value={i18n.language}
			onValueChange={(value) => i18n.changeLanguage(value)}
		>
			<Select.Trigger className="inline-flex items-center gap-2 px-3 py-2 rounded-lg bg-gray-100 dark:bg-gray-800 hover:bg-gray-200 dark:hover:bg-gray-700 transition-colors outline-none focus:ring-2 focus:ring-indigo-500">
				<CurrentFlag />
				<span className="text-sm font-medium text-gray-700 dark:text-gray-300">
					{currentLanguage.code.toUpperCase()}
				</span>
				<ChevronDown size={16} className="text-gray-500 dark:text-gray-400" />
			</Select.Trigger>

			<Select.Portal>
				<Select.Content
					className="overflow-hidden bg-white dark:bg-gray-800 rounded-lg shadow-lg border border-gray-200 dark:border-gray-700 z-[100]"
					position="popper"
					sideOffset={5}
				>
					<Select.Viewport className="p-1">
						{languages.map((lang) => {
							const Flag = lang.FlagComponent;
							return (
								<Select.Item
									key={lang.code}
									value={lang.code}
									className="relative flex items-center gap-3 px-3 py-2 rounded-md text-sm outline-none cursor-pointer select-none text-gray-700 dark:text-gray-200 hover:bg-gray-100 dark:hover:bg-gray-700 data-[state=checked]:bg-indigo-50 dark:data-[state=checked]:bg-indigo-900/20 transition-colors"
								>
									<Flag />
									<Select.ItemText>
										{lang.label}
									</Select.ItemText>
									<Select.ItemIndicator>
										<Check
											size={16}
											className="text-indigo-600 dark:text-indigo-400"
										/>
									</Select.ItemIndicator>
								</Select.Item>
							);
						})}
					</Select.Viewport>
				</Select.Content>
			</Select.Portal>
		</Select.Root>
	);
}
